+  @Test
+  public void testChannelSplittingWithAllowedSplitPoints() throws Exception {
+
+    List<WindowedValue<String>> outputValues = new ArrayList<>();
+    String pTransformId = "pTransformId";
+    BeamFnDataReadRunner<String> readRunner = createReadRunner(outputValues::add, pTransformId);
+    // 16 is the desired split point.
+    assertEquals(
+        channelSplitResult(16),
+        executeSplit(
+            readRunner,
+            pTransformId,
+            32,
+            Collections.EMPTY_LIST,
+            0.5,
+            ImmutableList.of(10L, 13L, 16L, 20L)));
+
+    // 8 should be the split but it's not in the allowedSplitPoints list. Then 8 is the closet one.
+    assertEquals(
+        channelSplitResult(9),
+        executeSplit(
+            readRunner,
+            pTransformId,
+            16,
+            Collections.EMPTY_LIST,
+            0.5,
+            ImmutableList.of(5L, 7L, 9L, 10L)));
+
+    // 4 is the computed result but it's not in the allowedSplitPoints list. 5 is the closet one.
+    assertEquals(
+        channelSplitResult(5),
+        executeSplit(
+            readRunner, pTransformId, 7, Collections.EMPTY_LIST, 0.5, ImmutableList.of(2L, 5L)));
+
+    // The desired split point is not in the range of the allowed list.
