+
+    @Parameterized.Parameters
+    public static Iterable<Object[]> data() {
+      return ImmutableList.<Object[]>builder()
+          // Split as close to the beginning as possible.
+          .add(new Object[] {channelSplitResult(1L), 16L, ImmutableList.of("A"), 0, 0})
+          // The closest split is at 4, even when just above or below it.
+          .add(new Object[] {channelSplitResult(4L), 16L, ImmutableList.of("A"), 0.24, 0})
+          .add(new Object[] {channelSplitResult(4L), 16L, ImmutableList.of("A"), 0.25, 0})
+          .add(new Object[] {channelSplitResult(4L), 16L, ImmutableList.of("A"), 0.26, 0})
+          // Split the *remainder* in half.
+          .add(new Object[] {channelSplitResult(8L), 16L, ImmutableList.of("A"), 0.5, 0})
+          .add(new Object[] {channelSplitResult(9L), 16L, ImmutableList.of("A", "B", "C"), 0.5, 0})
+          .add(
+              new Object[] {
+                channelSplitResult(11L),
+                16L,
+                ImmutableList.of("A", "B", "C", "D", "E", "F", "G"),
+                0.5,
+                0
+              })
+          // Progress into the active element influences where the split of the remainder falls.
+          .add(new Object[] {channelSplitResult(1L), 4L, ImmutableList.of("A"), 0.25, 0.5})
+          .add(new Object[] {channelSplitResult(2L), 4L, ImmutableList.of("A"), 0.25, 0.9})
+          .add(new Object[] {channelSplitResult(2L), 4L, ImmutableList.of("A", "B"), 0.25, 0.0})
+          .add(new Object[] {channelSplitResult(2L), 4L, ImmutableList.of("A", "B"), 0.25, 0.1})
+          .build();
+    }

