+        writeId = Long.parseLong(folder.substring(BASE_PREFIX.length()));
+      } else if (folder.startsWith(DELTA_PREFIX) || folder.startsWith(DELETE_DELTA_PREFIX)) {
+        // See AcidUtils.parseDelta
+        visibilityId = getVisibilityTxnId(folder);
+        if (visibilityId > 0) {
