public String getArchivePath() {
  String archiveFolder = tableConfig.getArchivelogFolder();
  if (archiveFolder.equals(HoodieTableConfig.DEFAULT_ARCHIVELOG_FOLDER)) {
    return getMetaPath();
  } else {
    return getMetaPath() + "/" + archiveFolder;
  }
}

