+    JavaPairRDD<String, String> partitionRecordKeyPairRDD = hoodieKeys.mapToPair(entry -> new Tuple2(entry.getPartitionPath(), entry.getRecordKey()));
+    jsc.setJobGroup(this.getClass().getSimpleName(), "Fetching fileInfos for partitions of interest");
+    List<String> affectedPartitionPathList = partitionRecordKeyPairRDD.map(tuple -> tuple._1).distinct().collect();
+    JavaRDD<Tuple2<String, String>> fileInfoList = jsc.parallelize(
+        loadAllFilesForPartitions(affectedPartitionPathList, jsc, hoodieTable)).sortBy(Tuple2::_1, true, config.getSimpleIndexParallelism());
+
+    JavaPairRDD<String, String> partitionFileIndexInfoPairRDD = fileInfoList.mapToPair(entry -> new Tuple2<>(entry._1, entry._2));
+
+    jsc.setJobGroup(this.getClass().getSimpleName(), "Fetching records from all files of interest");
+    return partitionFileIndexInfoPairRDD.flatMapToPair(
+        (PairFlatMapFunction<Tuple2<String, String>, HoodieKey, HoodieRecordLocation>) partitionPathFileId ->
+            new RecordFetcher(partitionPathFileId, hoodieTable).getResultSet()
