+
+  /**
+   * Fetch records from the given parquet file as {@link List} of {@link ParquetRowInfo}. Filter for candidates if set.
+   *
+   * @param filePath      The parquet file path.
+   * @param configuration configuration to build fs object
+   * @return {@link List} of {@link ParquetRowInfo} matching candidateRecordKeys
+   */
+  static List<ParquetRowInfo> fetchRecordKeyPartitionPathFromParquet(Configuration configuration, Path filePath,
+                                                                     String baseInstantTime,
+                                                                     String fileId, List<String> candidatesToFilter) {
+    List<ParquetRowInfo> parquetRowInfos = new ArrayList<>();
+    try {
+      if (!filePath.getFileSystem(configuration).exists(filePath)) {
+        return new ArrayList<>();
+      }
+
+      Configuration conf = new Configuration(configuration);
+      conf.addResource(FSUtils.getFs(filePath.toString(), conf).getConf());
+      Schema readSchema = HoodieAvroUtils.getRecordKeyPartitionPathSchema();
+      AvroReadSupport.setAvroReadSchema(conf, readSchema);
+      AvroReadSupport.setRequestedProjection(conf, readSchema);
+      ParquetReader reader = AvroParquetReader.builder(filePath).withConf(conf).build();
+      Object obj = reader.read();
+      while (obj != null) {
+        if (obj instanceof GenericRecord) {
+          String recordKey = ((GenericRecord) obj).get(HoodieRecord.RECORD_KEY_METADATA_FIELD).toString();
+          String partitionPath = ((GenericRecord) obj).get(HoodieRecord.PARTITION_PATH_METADATA_FIELD).toString();
+          if (candidatesToFilter.isEmpty() || candidatesToFilter.contains(recordKey)) {
+            parquetRowInfos.add(new ParquetRowInfo(recordKey, partitionPath, baseInstantTime, fileId));
+          }
+          obj = reader.read();
+        }
+      }
+    } catch (IOException e) {
+      throw new HoodieIOException("Failed to read from Parquet file " + filePath, e);
+    }
+    return parquetRowInfos;
+  }
