+            /*queryNodeId*/ 16 +
+            /*id*/ 8 +
+            /*logicalReads*/ 8 +
+            /*physicalReads*/ 8;
+
+        SegmentedRingByteBuffer.WriteSegment seg = reserveBuffer(OperationType.QUERY_READS, size);
