+            buf -> {
+                buf.put((byte)type.ordinal());
+                buf.putInt(textBytes.length);
+                buf.put(textBytes);
+                buf.putLong(id);
+                buf.putLong(startTime);
+                buf.putLong(duration);
+                buf.put(success ? (byte)1 : 0);
+            });
