+            return Objects.equal(partitionId, that.partitionId) &&
+                    Objects.equal(indexId, that.indexId) &&
+                    Objects.equal(fromBe, that.fromBe) &&
+                    Objects.equal(toBe, that.toBe);
+        }
+
+        @Override
+        public int hashCode() {
