+                String columnName = null;
+                if (condition instanceof BinaryPredicate) {
+                    BinaryPredicate binaryPredicate = (BinaryPredicate) condition;
+                    columnName = ((SlotRef) binaryPredicate.getChild(0)).getColumnName();
