+                    columnName = ((SlotRef) isNullPredicate.getChild(0)).getColumnName();
+                }
+                Column column = indexColNameToColumn.get(columnName);
+                if (column == null) {
+                    ErrorReport.reportDdlException(ErrorCode.ERR_BAD_FIELD_ERROR, columnName, indexName);
+                }
