+                    deleteInfos = Lists.newArrayList();
+                    dbToDeleteInfos.put(dbId, deleteInfos);
+                }
+                deleteInfos.add(deleteInfo);
+            } finally {
+                writeUnlock();
+            }
+        } finally {
+            db.writeUnlock();
+        }
+    }
