+            }
+
+            List<String> deleteConditions = Lists.newArrayList();
+
+            // pre check
+            checkDeleteV2(olapTable, partition, conditions, deleteConditions, true);
+
+            // generate label
+            String label = "delete_" + UUID.randomUUID();
+
