+                                    TTaskType.REALTIME_PUSH,
+                                    transactionId,
+                                    Catalog.getCurrentGlobalTransactionMgr().getTransactionIDGenerator().getNextTransactionId());
+                            pushTask.setIsSchemaChanging(false);
+                            pushTask.setCountDownLatch(countDownLatch);
