package org.apache.iotdb.db.integration;

import java.sql.Connection;
import java.sql.DriverManager;
import java.sql.ResultSet;
import java.sql.ResultSetMetaData;
import java.sql.Statement;
import org.apache.iotdb.jdbc.IoTDBSQLException;
import org.junit.Test;

public class IoTDBBenchMark {

  private static void hello()
      throws Exception {
    Class.forName("org.apache.iotdb.jdbc.IoTDBDriver");
    try (Connection connection = DriverManager.getConnection("jdbc:iotdb://127.0.0.1:6667/", "root", "root");
        Statement statement = connection.createStatement()) {
      try {
        statement.execute("SET STORAGE GROUP TO root.hello.world.boris.jack.lucy.trump.clinton.good.sg1");
        statement.execute(
            "CREATE TIMESERIES root.hello.world.boris.jack.lucy.trump.clinton.good.sg1.d1.s1 WITH DATATYPE=INT64, ENCODING=RLE, COMPRESSOR=SNAPPY");
        statement.execute(
            "CREATE TIMESERIES root.hello.world.boris.jack.lucy.trump.clinton.good.sg1.d1.s2 WITH DATATYPE=INT64, ENCODING=RLE, COMPRESSOR=SNAPPY");
        statement.execute(
            "CREATE TIMESERIES root.hello.world.boris.jack.lucy.trump.clinton.good.sg1.d1.s3 WITH DATATYPE=INT64, ENCODING=RLE, COMPRESSOR=SNAPPY");
      } catch (IoTDBSQLException e) {
        System.out.println(e.getMessage());
      }

      for(int j = 0; j <= 200; j++) {
        for (int i = 0; i <= 100000; i++) {
          statement.addBatch(
              "insert into root.hello.world.boris.jack.lucy.trump.clinton.good.sg1.d1(timestamp, s1, s2, s3) values("
                  + j * 100000 + i + "," + 1 + "," + 1 + "," + 1 + ")");
        }
        statement.executeBatch();
        statement.clearBatch();
      }

      ResultSet resultSet = statement.executeQuery("select * from root where time <= 10");
      if (resultSet != null) {
        System.out.println("--------------------------");
        final ResultSetMetaData metaData = resultSet.getMetaData();
        final int columnCount = metaData.getColumnCount();
        for (int i = 0; i < columnCount; i++) {
          System.out.print(metaData.getColumnLabel(i + 1) + " ");
        }
        System.out.println();
        while (resultSet.next()) {
          for (int i = 1; ; i++) {
            System.out.print(resultSet.getString(i));
            if (i < columnCount) {
              System.out.print(", ");
            } else {
              System.out.println();
              break;
            }
          }
        }
        System.out.println("--------------------------\n");
      }
    }
  }


  @Test
  public void hellok() throws Exception {
    long start = System.currentTimeMillis();
    hello();
    long end = System.currentTimeMillis();
    System.out.println(end - start);
  }

}

