tasksToPreviousClients.forEach((t, c) -> assignment.computeIfPresent(c, (k, v) -> { v.add(t);  return v;}));
unassignedTasksWithCaughtUpClients = new ArrayList<>(tasksToCaughtUpClients.keySet());
unassignedTasksWithCaughtUpClients.removeAll(tasksToPreviousClients.keySet());

