                    if (reporters.size() == 1) {



                        return reporters.get(0).report(this);



                    }



                    List<Future<RecordMetadata>> futures = new LinkedList<>();



                    for (ErrorReporter reporter: reporters) {



                        Future<RecordMetadata> future = reporter.report(this, callback);



                        if (!future.isDone()) {



