+            return new NoopTenantOperationImpl();
+        case SELECT:
+            return new QueryTenantOperationImpl();
+        case UPSERT:
+            return new UpsertTenantOperationImpl();
+        case USER_DEFINED:
+            return new UserDefinedOperationImpl();
+        default:
+            throw new IllegalArgumentException("Unknown operation type");
+        }
+    }
