+    //Note that local secondary indexes aren't supported because they go in the same WALEdit as the
+    // "base" table data they index.
+
+    private void upsertAndDeleteHelper(TestTableInfo tableInfo, boolean createGlobalIndex) throws SQLException {
+        try (Connection conn = getConnection()) {
+            ddlUtil.createBaseTable(conn, tableInfo.getSchemaName(), tableInfo.getTableName(),
+                isMultiTenant, null, null, isImmutable);
+            if (createGlobalIndex) {
+                ddlUtil.createIndex(conn, tableInfo.getSchemaName(), tableInfo.getIndexName(),
+                    tableInfo.getTableName(), "v1", false, false);
+            }
+            String upsertSql = "UPSERT INTO " + tableInfo.getFullTableName() + " VALUES" +
+                " ('a', 'b', 2, 'bc', 3)";
+            conn.createStatement().execute(upsertSql);
+            conn.commit();
+            //Deleting by entire PK gets executed as more like an UPSERT VALUES than an UPSERT SELECT
+            //(i.e, it generates the Mutations and then pushes them to server, rather than
+            // running a select query and deleting the mutations returned)
+            String deleteSql = "DELETE FROM " + tableInfo.getFullTableName() + " " +
+                "WHERE t_id = 'a' AND k1 = 'b'";
+            conn.createStatement().execute(deleteSql);
+            conn.commit();
+        }
+    }
