+    @Test
+    public void testUpsertSelectClientSide() throws Exception {
+        Assume.assumeTrue(HbaseCompatCapabilities.hasPreWALAppend());
+        TestTableInfo baseTableInfo = new TestTableInfo();
+        TestTableInfo targetTableInfo = new TestTableInfo();
+        long minTimestamp = EnvironmentEdgeManager.currentTimeMillis();
+        try (Connection conn = getConnection()) {
+            //upsert selecting from a different table will force processing to be client-side
+            ddlUtil.createBaseTable(conn, baseTableInfo.getSchemaName(), baseTableInfo.getTableName(),
+                isMultiTenant, null, null, isImmutable);
+            conn.createStatement().execute("UPSERT INTO " + baseTableInfo.getFullTableName() + " VALUES" +
+                " ('a', 'b', 2, 'bc', 3)");
+            conn.commit();
+            ddlUtil.createBaseTable(conn, targetTableInfo.getSchemaName(),
+                targetTableInfo.getTableName(), isMultiTenant, null, null, isImmutable);
+            String sql = "UPSERT INTO " + targetTableInfo.getFullTableName() +
+                " (t_id, k1, k2, v1, v2) SELECT * FROM " + baseTableInfo.getFullTableName();
+            conn.createStatement().execute(sql);
+            conn.commit();
+        }
+        assertAnnotation(1, baseTableInfo.getPhysicalTableName(), null,
+            baseTableInfo.getSchemaName(), baseTableInfo.getTableName(), PTableType.TABLE,
