+    LOGGER.info("Refreshing segment: {} for table: {}", segment, tableNameWithType);
+    RoutingEntry routingEntry = _routingEntryMap.get(tableNameWithType);
+    if (routingEntry != null) {
+      routingEntry.refreshSegment(segment);
