+        int result = 0;
+        for (ExpressionSegment each : valueExpressions) {
+            if (valueExpression == each) {
+                return result;
+            }
+            if (each instanceof ParameterMarkerExpressionSegment) {
+                result++;
+            }
+        }
+        throw new IllegalArgumentException("Can not get parameter index.");
