public final class ForEach {
     private ForEach() {}

     <I, O> List<O> inList(List<I> in, Mapping<I, O> mapping) {
         final List<O> out = new ArrayList<>();
         for (I item : in) {
             out.add(mapping.apply(item));
         }
         return Immutable.of(out);
     }

     @FunctionalInterface
     interface Mapping<I, O> {
