}

final class Bar {
    private final Foo foo;

    Bar() {
        this.foo = new Foo();
    }
}

public final class FooTest {
    @Before public void setup() {
        this.mockDependency = mock(Dependency.class);
        this.foo = new Foo(mockDependency);
    }
}

