final class MutableSyncExpression implements DataStoreSyncExpression {
    private QueryPredicate queryPredicate;

    MutableSyncExpression() {
        this.queryPredicate = QueryPredicates.all();
    }

    @Override public QueryPredicate resolvePredicate() {
        return queryPredicate;
    }

    void updatePredicate(QueryPredicate queryPredicate) {
        this.queryPredicate = queryPredicate;
    }
    // equals/hashCode/toString ...
}

