+            AggregatedMetric.Metric m = AggregatedMetric.Metric.builder()
+                    .name(metricName)
+                    .unit(metrics.get(0).getUnit())
+                    .value(value)
+                    .build();
+            aggMetrics.add(m);
+        }
+        return aggMetrics;
+    }
