+
+import java.io.File;
+import java.io.IOException;
+import java.nio.charset.StandardCharsets;
+import java.nio.file.Files;
+import java.nio.file.Path;
+import java.util.ArrayList;
+import java.util.List;
+import java.util.Optional;
+import java.util.Set;
+
+import static java.nio.file.StandardCopyOption.REPLACE_EXISTING;
+
+/**
+ * Local Store Implementation for Evergreen Packages.
+ */
+public class LocalPackageStore implements PackageStore {
+    private final Path cacheFolder;
+
+    private static Path getPackageStorageRoot(final String packageName,
+                                             final Path cacheFolder) {
+        return cacheFolder.resolve(packageName);
+    }
+
+    private static Path getPackageVersionStorageRoot(final PackageRecipe curPackage, final Path cacheFolder) {
+        return getPackageVersionStorageRoot(curPackage.getPackageName(), curPackage.getPackageVersion().toString(),
+                                            cacheFolder);
+    }
+
+    private static Path getPackageVersionStorageRoot(final String packageName, final String packageVersion,
+                                              final Path cacheFolder) {
+        return getPackageStorageRoot(packageName, cacheFolder).resolve(packageVersion);
+    }
+
+    public LocalPackageStore(Path cacheFolder) {
+        this.cacheFolder = cacheFolder;
+    }
+
+    /**
+     * Get package from cache if it exists.
+     * @return
+     */
+    @Override
+    public Optional<String> getPackageRecipeIfExists(final String packageName,
+                                                     final Semver packageVersion) {
+        Path srcPkgRoot = getPackageVersionStorageRoot(packageName, packageVersion.toString(),
+                                                       cacheFolder);
+
+        if (!Files.exists(srcPkgRoot) || !Files.isDirectory(srcPkgRoot)) {
+            return Optional.empty();
+        }
+        // TODO: Move to a Common list of Constants
+        Path recipePath = srcPkgRoot.resolve(Constants.RECIPE_FILE_NAME);
+
+        if (!Files.exists(recipePath) && Files.isRegularFile(recipePath)) {
+            throw new RuntimeException("Package manager cache is corrupt");
+            // TODO Take some corrective actions before throwing
+        }
+
+        try {
+            return Optional.of(new String(Files.readAllBytes(recipePath), StandardCharsets.UTF_8));
+        } catch (IOException e) {
+            return Optional.empty();
+        }
+    }
+
+    /**
+     * Get package from cache if it exists.
+     */
+    @Override
+    public List<Semver> getPackageVersionsIfExists(final String packageName)
+            throws UnexpectedPackagingException {
+        Path srcPkgRoot = getPackageStorageRoot(packageName, cacheFolder);
+        List<Semver> versions = new ArrayList<>();
+
+        if (!Files.exists(srcPkgRoot) || !Files.isDirectory(srcPkgRoot)) {
+            return versions;
+        }
+
+        File[] versionDirs = srcPkgRoot.toFile().listFiles();
+        if (versionDirs == null || versionDirs.length == 0) {
+            return versions;
+        }
+
+        try {
+            for (File versionDir : versionDirs) {
+                versions.add(new Semver(versionDir.getName(), Semver.SemverType.NPM));
+            }
+        } catch (SemverException e) {
+            throw new UnexpectedPackagingException("Package Cache is corrupted! " + e.toString());
+        }
+
+        return versions;
+    }
+
+    /**
+     * Get package from cache if it exists.
+     */
+    @Override
+    public Optional<Semver> getLatestPackageVersionIfExists(final String packageName) {
+        // Add check for package override
+
+        try {
+            List<Semver> versions = getPackageVersionsIfExists(packageName);
+            versions.sort(null);
+
+            return Optional.ofNullable(versions.get(0));
+        } catch (UnexpectedPackagingException e) {
+            return Optional.empty();
+        }
+    }
+
+    /**
+     * Cache all artifacts for a given Package.
+     */
+    @Override
+    public void cachePackageArtifacts(final PackageRecipe curPackage) throws PackagingException {
+        if (curPackage == null) {
+            // TODO: Check and handle correctly
+            throw new NullPointerException("Package cannot be null");
+        }
+
+        Path destRootPkgPath = getPackageVersionStorageRoot(curPackage, cacheFolder);
+
+        try {
+            if (Files.notExists(destRootPkgPath)) {
+                Files.createDirectories(destRootPkgPath);
+            }
+            Set<ArtifactProvider> artifacts = curPackage.getArtifactProviders();
+            for (ArtifactProvider artifactUrl : artifacts) {
+                artifactUrl.downloadArtifactToPath(destRootPkgPath);
+            }
+        } catch (IOException e) {
+            throw new PackagingException("Failed to download artifacts for " + curPackage.getPackageName());
+        }
+    }
