+            }
+        }
+
+        if (applicationProperties != null) {
+            final Map<String, Object> map = applicationProperties.getValue();
+
+            for (Map.Entry<String, Object> entry : map.entrySet()) {
+                final int size = sizeof(entry.getKey()) + sizeof(entry.getValue());
