+    public TracerProvider(Iterable<Tracer> tracers) {
+        Objects.requireNonNull(tracers, "'tracers' cannot be null.");
+        tracers.forEach(this.tracers::add);
+        isEnabled = this.tracers.size() > 0;
+    }
+
