+            IntelliJAuthMethodDetails authDetails = cacheAccessor.getAuthDetailsIfAvailable();
+            String authType = authDetails.getAuthMethod();
+            if (authType.equalsIgnoreCase("SP")) {
+                Map<String, String> spDetails = cacheAccessor
