+import static org.ballerinalang.cli.module.util.Utils.convertToUrl;
+import static org.ballerinalang.cli.module.util.Utils.createHttpUrlConnection;
+import static org.ballerinalang.cli.module.util.Utils.initializeSsl;
+import static org.ballerinalang.cli.module.util.Utils.setRequestMethod;
+
+/**
+ * This class is pulling the modules from ballerina central.
+ */
+public class Pull {
+    private static final String VERSION_REGEX = "(\\d+\\.)(\\d+\\.)(\\d+)";
+    private static PrintStream outStream = System.out;
+    private static DefaultLogFormatter logFormatter = new DefaultLogFormatter();
+
+    private Pull() {
+    }
+
+    public static void execute(String url, String modulePathInBaloCache, String modulePath, String proxyHost,
+            int proxyPort, String proxyUsername, String proxyPassword, String supportedVersionRange, boolean isBuild,
+            String nightlyBuild, String langSpecVersion, String platform) {
+        if (isBuild) {
+            logFormatter = new BuildLogFormatter();
+        }
+
+        initializeSsl();
+        HttpURLConnection conn = createHttpUrlConnection(convertToUrl(url + supportedVersionRange), proxyHost,
+                proxyPort, proxyUsername, proxyPassword);
+        conn.setInstanceFollowRedirects(false);
+        setRequestMethod(conn, Utils.RequestMethod.GET);
+
+        // set headers.
+        if (!platform.equals("")) {
+            conn.setRequestProperty("Ballerina-Platform", platform);
+        }
+        if (!langSpecVersion.equals("")) {
+            conn.setRequestProperty("Ballerina-Language-Specification-Version", langSpecVersion);
+        }
+        conn.setRequestProperty("Accept-Encoding", "identity");
+
+        boolean redirect = false;
+        try {
+            int statusCode = conn.getResponseCode();
+
+            if (Integer.toString(statusCode).startsWith("5")) {
+                createError("unable to connect to remote repository: " + url);
+            } else if (statusCode != HttpURLConnection.HTTP_OK) {
+                if (statusCode == HttpURLConnection.HTTP_MOVED_TEMP || statusCode == HttpURLConnection.HTTP_MOVED_PERM
+                        || statusCode == HttpURLConnection.HTTP_SEE_OTHER) {
+                    redirect = true;
+                } else {
+                    handleErrorResponse(url, modulePath, isBuild, conn, statusCode);
+                }
+            }
+
+            if (redirect) {
+                // get redirect url from "location" header field
+                String newUrl = conn.getHeaderField("Location");
+                String contentDisposition = conn.getHeaderField("Content-Disposition");
+
+                conn = createHttpUrlConnection(convertToUrl(newUrl), proxyHost, proxyPort, proxyUsername,
+                        proxyPassword);
+                conn.setRequestProperty("Content-Disposition", contentDisposition);
+
+                String resolvedURI = conn.getHeaderField("RESOLVED_REQUESTED_URI");
+                if (resolvedURI == null || resolvedURI.equals("")) {
+                    resolvedURI = newUrl;
+                }
+                String[] uriParts = resolvedURI.split("/");
+                String moduleVersion = uriParts[uriParts.length - 3];
+                boolean valid = moduleVersion.matches(VERSION_REGEX);
+
+                if (valid) {
+                    String baloFile = setFileNameFromContentDispositionHeader(contentDisposition,
+                            uriParts[uriParts.length - 1]);
+                    Path baloCacheWithModulePath = Paths.get(modulePathInBaloCache, moduleVersion);
+                    //<user.home>.ballerina/balo_cache/<org-name>/<module-name>/<module-version>
+
+                    Path baloPath = Paths.get(baloCacheWithModulePath.toString(), baloFile);
+                    if (baloPath.toFile().exists()) {
+                        createError("module already exists in the home repository: " + baloPath.toString());
+                    }
+
+                    createBaloFileDirectory(baloCacheWithModulePath);
+                    copyBaloFile(modulePath, conn, moduleVersion, baloPath);
+
+                    if (nightlyBuild.equals("true")) {
+                        // If its a nightly build tag the file as a module from nightly
+                        Path nightlyBuildMetaFile = Paths.get(baloCacheWithModulePath.toString(), "nightly.build");
+                        if (!Paths.get(nightlyBuild).toFile().exists()) {
+                            createNightlyBuildMetaFile(nightlyBuildMetaFile);
+                        }
+                    }
+                } else {
+                    createError("module version could not be detected");
+                }
+            }
+        } catch (IOException e) {
+            createError("connection to the remote repository host failed: " + e.getMessage());
+        }
+        conn.disconnect();
+        Authenticator.setDefault(null);
+    }
+
+    private static void createNightlyBuildMetaFile(Path nightlyBuildMetaFile) {
+        try {
+            Files.createFile(nightlyBuildMetaFile);
+        } catch (Exception e) {
+            createError("error occurred while creating nightly.build file.");
+        }
+    }
+
+    private static void copyBaloFile(String modulePath, HttpURLConnection conn, String moduleVersion, Path baloPath) {
+        try (InputStream inputStream = conn.getInputStream();
+                FileOutputStream outputStream = new FileOutputStream(baloPath.toString())) {
+            byte[] buffer = new byte[1024];
+            int count;
+
+            long totalSizeInKB = conn.getContentLengthLong() / 1024;
+            try (ProgressBar progressBar = new ProgressBar(
+                    modulePath + ":" + moduleVersion + " [central.ballerina.io -> home repo] ",
+                    totalSizeInKB, 1000, outStream, ProgressBarStyle.ASCII, " KB", 1)) {
+                while ((count = inputStream.read(buffer)) > 0) {
+                    outputStream.write(buffer, 0, count);
+                    progressBar.step();
+                }
+            }
+        } catch (IOException e) {
+            createError("error occurred copying the balo file: " + e.getMessage());
+        }
+    }
+
+    private static void createBaloFileDirectory(Path baloCacheWithModulePath) {
+        try {
+            Files.createDirectory(baloCacheWithModulePath);
+        } catch (Exception e) {
+            createError("error creating directory for balo file");
+        }
+    }
+
+    private static void handleErrorResponse(String url, String modulePath, boolean isBuild, HttpURLConnection conn,
+            int statusCode) {
+        try (BufferedReader reader = new BufferedReader(
+                new InputStreamReader(conn.getInputStream(), Charset.defaultCharset()))) {
+            StringBuilder result = new StringBuilder();
+            String line;
+            while ((line = reader.readLine()) != null) {
+                result.append(line);
+            }
+
+            MapValue payload = (MapValue) JSONParser.parse(result.toString());
+            String errMsg = payload.getStringValue("message");
+
+            if (statusCode == HttpURLConnection.HTTP_NOT_FOUND && isBuild && errMsg
+                    .contains("module not found")) {
+                // To ignore printing the error
+                createError("");
+            } else {
+                createError(errMsg);
+            }
+        } catch (IOException e) {
+            createError("failed to pull the module '" + modulePath + "' from the remote repository '" + url
+                    + "'");
+        }
+    }
+
+    private static void createError(String errMessage) {
+        throw ErrorUtil.createCommandException(logFormatter.formatLog(errMessage));
+    }
+
