+/**
+ * This class is pushing the modules to ballerina central.
+ */
+public class Push {
+
+    private static PrintStream errStream = System.err;
+    private static PrintStream outStream = System.out;
+
+    private Push() {
+    }
+
+    public static void execute(String url, String proxyHost, int proxyPort, String proxyUsername, String proxyPassword,
+            String accessToken, String orgName, String moduleName, String version, Path baloPath) {
+        initializeSsl();
+        HttpURLConnection conn = createHttpUrlConnection(convertToUrl(url), proxyHost, proxyPort, proxyUsername,
+                proxyPassword);
+        conn.setInstanceFollowRedirects(false);
+        setRequestMethod(conn, Utils.RequestMethod.POST);
+
+        // set headers.
+        conn.setRequestProperty("Authorization", "Bearer " + accessToken);
+        conn.setRequestProperty("Push-Organization", orgName);
+        conn.setRequestProperty("Content-Type", "application/octet-stream");
+
+        conn.setDoOutput(true);
+        conn.setChunkedStreamingMode(1024);
+
+        byte[] baloContent;
+        try {
+            baloContent = Files.readAllBytes(baloPath);
+        } catch (IOException e) {
+            throw ErrorUtil.createCommandException("cannot read the balo content");
+        }
+
+        try (DataOutputStream outputStream = new DataOutputStream(conn.getOutputStream())) {
+            // send post request.
+            byte[] buffer = new byte[1024];
+            int count;
+            long totalSizeInMB = baloContent.length / 1024;
+
+            try (ProgressBar progressBar = new ProgressBar(
+                    orgName + "/" + moduleName + ":" + version + " [project repo -> central]", totalSizeInMB, 1000,
+                    outStream, ProgressBarStyle.ASCII, " KB", 1);
+                    FileInputStream fis = new FileInputStream(baloPath.toFile())) {
+                while ((count = fis.read(buffer)) > 0) {
+                    outputStream.write(buffer, 0, count);
+                    outputStream.flush();
+                    progressBar.step();
+                }
+            }
+        } catch (FileNotFoundException e) {
+            throw ErrorUtil.createCommandException("cannot read the balo content");
+        } catch (IOException e) {
+            throw ErrorUtil.createCommandException("error occurred while uploading balo to central: " + e.getMessage());
+        }
+
+        try {
+            int statusCode = conn.getResponseCode();
+
+            if (Integer.toString(statusCode).startsWith("5")) {
+                errStream.println("unable to connect to remote repository: " + url);
+            } else if (statusCode == HttpURLConnection.HTTP_UNAUTHORIZED) {
+                errStream.println("unauthorized access token for organisation: " + orgName);
+            } else if (statusCode != HttpURLConnection.HTTP_OK) {
+                try (BufferedReader errorStream = new BufferedReader(
+                        new InputStreamReader(conn.getInputStream(), Charset.defaultCharset()))) {
+                    String errorContent = errorStream.lines().collect(Collectors.joining("\n"));
+                    errStream.println(errorContent);
+                }
+            } else {
+                outStream.println(orgName + "/" + moduleName + ":" + version + " pushed to central successfully");
+            }
+        } catch (IOException e) {
+            throw ErrorUtil.createCommandException(e.getMessage());
+        }
+        conn.disconnect();

