+    private static String getStringNullChecked(String string) {
+        if (string == null) {
+            return "";
+        } else {
+            return string;
+        }
+    }
+
+    private static int getNullArrayLengthChecked(Address[] addresses) {
+        if (addresses == null) {
+            return 0;
+        } else {
+            return addresses.length;
+        }
+    }
