+                equal = false;
+                description = "One of the values is null";
+            }
+        } else {
+            if (value1 instanceof Number) {
+                double numValue1 = ((Number) value1).doubleValue();
+                double numValue2 = ((Number) value2).doubleValue();
+                double relativeChange = (numValue1 == 0 && numValue2 == 0) ? 0 : (numValue1 == 0) ? Double.MAX_VALUE : (numValue1 - numValue2) / numValue2;
+                if (relativeChange != 0) {
+                    if (MetricToAllowableRelativeChange.containsKey(metricName)) {
+                        double allowableRelativeChange = MetricToAllowableRelativeChange.get(metricName);
+                        if (abs(relativeChange) >= allowableRelativeChange) {
+                            equal = false;
+                            description = "Changed by " + RELATIVE_CHANGE_DECIMAL_FORMAT.format(relativeChange) +
+                                    " which is outside of the allowable tolerance of " + RELATIVE_CHANGE_DECIMAL_FORMAT.format(allowableRelativeChange);
+                        } else {
+                            equal = true;
+                            description = "Changed by " + RELATIVE_CHANGE_DECIMAL_FORMAT.format(relativeChange) +
+                                    " which is within the allowable tolerance of " + RELATIVE_CHANGE_DECIMAL_FORMAT.format(allowableRelativeChange);
+                        }
+                    } else {
+                        equal = false;
+                        description = "Changed by " + RELATIVE_CHANGE_DECIMAL_FORMAT.format(relativeChange);
+                    }
+                }
+            } else {
+                if (!value1.equals(value2)) {
+                    equal = false;
+                    description = "Different Values";
+                }
+            }
+        }
