+package org.candlepin.dto.api.v1;
+
+import org.candlepin.dto.ModelTranslator;
+import org.candlepin.dto.ObjectTranslator;
+import org.candlepin.model.Entitlement;
+import org.candlepin.util.Util;
+
+/**
+ * The EntitlementTranslator provides translation from Entitlement model objects to EntitlementDTOs.
+ */
+public class NestedEntitlementTranslator implements ObjectTranslator<Entitlement, NestedEntitlementDTO> {
+
+    /**
+     * {@inheritDoc}
+     */
+    @Override
+    public NestedEntitlementDTO translate(Entitlement source) {
+        return this.translate(null, source);
+    }
+
+    /**
+     * {@inheritDoc}
+     */
+    @Override
+    public NestedEntitlementDTO translate(ModelTranslator translator, Entitlement source) {
+        return source != null ? this.populate(translator, source, new NestedEntitlementDTO()) : null;
+    }
+
+    /**
+     * {@inheritDoc}
+     */
+    @Override
+    public NestedEntitlementDTO populate(Entitlement source, NestedEntitlementDTO destination) {
+        return this.populate(null, source, destination);
+    }
+
+    /**
+     * {@inheritDoc}
+     */
+    @Override
+    public NestedEntitlementDTO populate(
+        ModelTranslator modelTranslator, Entitlement source, NestedEntitlementDTO dest) {
+        if (source == null) {
+            throw new IllegalArgumentException("source is null");
+        }
+
+        if (dest == null) {
+            throw new IllegalArgumentException("destination is null");
+        }
+
+        dest.setId(source.getId());
+        dest.setQuantity(source.getQuantity());
+        dest.setDeletedFromPool(source.deletedFromPool());
+        dest.setStartDate(Util.toDateTime(source.getStartDate()));
+        dest.setEndDate(Util.toDateTime(source.getEndDate()));

