+    //Subsequent executions will not run concurrently (scheduler guarantee),
+    // so need not worry about scan already in progress.
+    LOG.debug("Scanning capability config directory");
+    try {
+      scanConfigDirectory();
+    } catch (Exception exception) {
+      //If exception is thrown, no more executions will happen.
+      LOG.error("Error while scanning capability config directory ", exception);
+    }
+  }
