+      CapabilityConfig capabilityConfig = parseConfig(configFile);
+      if (toDelete.contains(configFile)) {
+        handleDelete(capabilityConfig, configFile);
+        continue;
+      }
+      capabilityExecutorMap.get(capabilityConfig.getType()).execute(capabilityConfig);
+    }
+  }
+
+  private void handleDelete(CapabilityConfig capabilityConfig, File configFile) {
+    capabilityExecutorMap.get(CapabilityActionType.DELETE).execute(capabilityConfig);
+    String capability = capabilityConfig.getCapability();
+    if (capabilityManager.isCapabilityPresent(capability)) {
+      return;
+    }
+    //capability deleted. Remove the file
+    LOG.debug("Cleaning up the config file {} after removing capability {}", configFile, capability);
+    configFile.delete();
+  }
