+        } catch (IOException ex) {
+          LOG.error("Reading capability config file {} failed with {}", configFile, ex);
+        }
+      }
+    } finally {
+      readLock.unlock();
+    }
+    refreshCapabilities(allCapabilityConfigs);
+  }
