+      LOG.debug("Applying {} action for capability {}", config.getType(), capability);
+      switch (config.getType()) {
+        case ENABLE:
+          deployAllApps(capability, config.getApplications());
+          config.getPrograms().forEach(systemProgram -> enabledPrograms
