removeFolderWithProtectedFiles() {
  while [[ "$PWD" != '/' && ${PWD##*/} != "$1" ]];
  do
    cd ..
    echo "${PWD##*/}"
  done
  if [[ "${PWD##*/}" == "$1" ]]; then
    find . -delete
    cd ..
    rmdir "$1"
    echo "$1 folder removed..."
    return 0;
  fi
  echo "unable to remove $1 folder"
}
