+public class ModuleHooksAggregator {
+
+    private static final byte MAJOR_SCHEMA_VERSION_THREE = 3;
+
+    private final DelegateExecution execution;
+    private final Module moduleToDeploy;
+
+    public ModuleHooksAggregator(DelegateExecution execution, Module moduleToDeploy) {
+        this.execution = execution;
+        this.moduleToDeploy = moduleToDeploy;
+    }
+
+    public List<Hook> aggregateHooks(HookPhase currentHookPhaseForExecution) {
+        substituteBeforeStartHooksPhases();
+        Map<String, List<String>> alreadyExecutedHooksForModule = getAlreadyExecutedHooks();
+        List<Hook> hooksCalculatedForExecution = determineHooksForExecution(alreadyExecutedHooksForModule, currentHookPhaseForExecution);
+        updateExecutedHooksForModule(alreadyExecutedHooksForModule, currentHookPhaseForExecution, hooksCalculatedForExecution);
+        return hooksCalculatedForExecution;
+    }
+
+    private void substituteBeforeStartHooksPhases() {
+        for (Hook hook : getModuleHooks(moduleToDeploy)) {
+            List<String> phases = new ArrayList<>(hook.getPhases());
+            if (phases.contains(HookPhase.APPLICATION_BEFORE_START.getValue())) {
+                phases.remove(HookPhase.APPLICATION_BEFORE_START.getValue());
+                phases.add(HookPhase.APPLICATION_BEFORE_START_LIVE.getValue());
+                phases.add(HookPhase.APPLICATION_BEFORE_START_IDLE.getValue());
+                hook.setPhases(phases);
+            }
+        }
+    }
