+    config.addAllowedHeader("Content-Type");
+    config.addAllowedOrigin("*");
+    config.addExposedHeader("Location");
+    config.addExposedHeader("Content-Location");
+    config.setAllowedMethods(Arrays.asList("GET", "POST", "PUT", "DELETE", "OPTIONS"));

