+
+    for (int i = 0; i < tokens; i++) {
+      final String symbolicName = vocabulary.getSymbolicName(i);
+      if (symbolicName != null) {
+        final String keyWord = symbolicName.toLowerCase();
+        builder.add(keyWord);
