+    UnsignedLong latestBlockNumber = UnsignedLong.valueOf(latestBlock.getNumber());
+    exploreBlocksInDirection(latestBlockNumber, true).reportExceptions();
+  }
+
+  public void runCacheStartup() {
+    doCacheStartup()
