+  @Override
+  protected SafeFuture<?> doStop() {
+    LOG.debug("Stop {}", getClass().getSimpleName());
+    syncStateProvider.unsubscribeFromSyncStateChanges(syncStateSubscription.get());
+    badPeerCache.clear();
+    return SafeFuture.COMPLETE;
+  }
