+plugins {
+	id 'java'
+	id 'application'
+
+	id 'com.diffplug.gradle.spotless' version '4.3.0'
+	id 'pl.allegro.tech.build.axion-release' version '1.10.3'
+}
+
+repositories {
+	mavenLocal()
+	jcenter()
+	mavenCentral()
+}
+
+spotless {
+	java {
+		googleJavaFormat()
+	}
+	groovyGradle {
+		target '*.gradle'
+		greclipse()
+	}
+	format 'misc', {
+		target '.gitignore'
+		indentWithSpaces(2)
+		trimTrailingWhitespace()
+		endWithNewline()
+	}
+}
+task formatCode(dependsOn: ['spotlessApply'])
+
+scmVersion {
+	tag {
+		prefix = 'v'
+		versionSeparator = ''
+	}
+
+	versionIncrementer 'incrementMinor'
+
+	checks {
+		uncommittedChanges = false
+		aheadOfRemote = true
+		snapshotDependencies = false
+	}
+
+	repository {
+		directory = project.rootProject.file('../')
+	}
+}
+
+def dd_trace_version = scmVersion.version
+
+dependencies {
+	implementation "com.datadoghq:dd-java-agent:${dd_trace_version}"
+	implementation "com.datadoghq:dd-trace-api:${dd_trace_version}"
+	implementation "com.datadoghq:dd-trace-java:${dd_trace_version}"

