+
+
+    public Hex subtract(Hex b) {
+        return new Hex(q - b.q, r - b.r, s - b.s);
+    }
+
+
+    public Hex scale(int k) {
+        return new Hex(q * k, r * k, s * k);
+    }
+
+
+    public Hex rotateLeft() {
+        return new Hex(-s, -q, -r);
+    }
+
+
+    public Hex rotateRight() {
+        return new Hex(-r, -s, -q);
