+#include "optimizer/HotFieldMarking.hpp"
+#include "env/j9method.h"
+#include "il/Block.hpp"
+#include "il/Node.hpp"
+#include "il/Node_inlines.hpp"
+#include "il/SymbolReference.hpp"
