+package org.eclipse.hono.util;
+
+import java.util.Objects;
+import java.util.regex.PatternSyntaxException;
+
+/**
+ * Utility class for handling AMQP addresses.
+ */
+public class AddressHelper {
+
+    private AddressHelper() {
+    }
+
+    /**
+     * Gets the AMQP <em>target</em> address to use for creating with specific Hono endpoint.
+     *
+     * @param endpoint The endpoint for the address (e.g. telemetry).
+     * @param tenantId The tenant of the address.
+     * @param resourceId The device to upload data for. If {@code null}, the target address can be used
+     *                 to upload data for arbitrary devices belonging to the tenant.
+     * @param addressRewriteRule The rewrite rule to be applied to the address.
+     *                           See {@link #rewrite(String, String, String)} for more information about syntax and behavior of this property.
+     * @return The target address.
+     * @throws NullPointerException if endpoint or tenant is {@code null}.
+     */
+    public static String getTargetAddress(final String endpoint, final String tenantId, final String resourceId, final String addressRewriteRule) {
+        final StringBuilder addressBuilder = new StringBuilder(Objects.requireNonNull(endpoint))
+                .append("/").append(Objects.requireNonNull(tenantId));
+        if (!Strings.isNullOrEmpty(resourceId)) {
+            addressBuilder.append("/").append(resourceId);
+        }
+
+        return AddressHelper.rewrite(endpoint, addressBuilder.toString(), addressRewriteRule);
+    }
+
+    /**
+     * Rewrites address for a certain endpoint according to the provided rule.
+     *
+     * @param endpoint The messaging endpoint.
+     * @param address The address to be rewritten.
+     * @param rewriteRule The rewrite rule. The rule is in the <em>$PATTERN $SUBSTITUTION</em> format.
+     *                    The <em>$PATTERN</em> should match the original address. It uses the regular Java pattern syntax. There
+     *                    are two helper constants introduced:
+     *                    <ul>
+     *                      <li><em>ENDPOINT</em> - that will match the current endpoint being used</li>
+     *                      <li><em>ID</em> - a pattern for id representations in the system</li>
+     *                    </ul>
+     *
+     *                    Example:
+     *                    <em>(${ENDPOINT})/(${ID}) test-vhost/$1/$2</em>
+     *                    will rewrite
+     *                    <em>telemetry/DEFAULT_TENANT</em> to <em>test-vhost/telemetry/DEFAULT-TENANT</em>
+     *                    If the rule is {@code null}, in the wrong format or the pattern doesn't match the address,
+     *                    the original address will be returned.
+     * @return The address to be used.
+     */
+    public static String rewrite(final String endpoint, final String address, final String rewriteRule) {
+        if (Strings.isNullOrEmpty(rewriteRule)) {
+            return address;
+        }
+
+        final String[] elements = rewriteRule.split(" ", 2);
+        if (elements.length != 2) {
+            return address;
+        }
+        return rewrite(endpoint, address, elements[0], elements[1]);
+    }
+
+    static String rewrite(final String endpoint, final String address, final String regex, final String replacement) {
+        try {
+            return address.replaceAll(regexSubstitues(endpoint, regex), replacement);
+        } catch (PatternSyntaxException pe) {
+            return address;
+        }
+    }
+
+    static String regexSubstitues(final String endpoint, final String regex) {
+        return regex
+                .replaceAll("\\$\\{ENDPOINT\\}", endpoint)
+                .replaceAll("\\$\\{ID\\}", RegistryManagementConstants.DEFAULT_ID_REGEX);

