+        assertEquals(counters.get(TestV2.A), oldCounters.get(TestV1.A));
+        assertEquals(counters.get(TestV2.B), oldCounters.get(TestV1.B));
+        assertEquals(counters.get(TestV2.C), oldCounters.get(TestV1.C));
+    }
